mapper.select:
select d.post_id postId, p.post_name postName, p.post_type postType, d.is_default isDefault from hub_dept_post d join hub_post p on d.post_id = p.post_id where dept_id = #{query.deptId} and p.tenant_id = #{tenantId}
and p.post_name like concat('%', #{query.postName}, '%')
and p.post_type = #{query.postType}
order by p.post_type

with pids as (select post_id from hub_post except select post_id from hub_dept_post where dept_id = #{query.deptId}) select p.post_id postId, p.post_name postName, p.post_type postType from pids join hub_post p on pids.post_id = p.post_id where p.tenant_id = #{tenantId}
and p.post_name like concat('%', #{query.postName}, '%')
and p.post_type = #{query.postType}
order by p.post_type

select DISTINCT on(u.user_id) u.user_id userId, u.user_name userName, u.user_phone userPhone, u.user_rank userRank, ud.is_default isDefault, ud.is_leader isLeader, p.post_name postName from hub_user_dept ud join hub_user u on ud.user_id = u.user_id left join hub_post p on ud.post_id = p.post_id where ud.dept_id = #{query.deptId} and u.tenant_id = #{tenantId}
and u.user_name like concat('%', #{query.userName}, '%')
and u.user_phone like concat('%', #{query.userPhone}, '%')

with uids as (select user_id from hub_user except select user_id from hub_user_dept where dept_id = #{query.deptId}) select DISTINCT on(u.user_id) u.user_id userId, u.user_name userName, u.user_phone userPhone, u.user_rank userRank, 0 as isDefault, 0 as isLeader from uids join hub_user u on uids.user_id = u.user_id where u.tenant_id = #{tenantId}
and u.user_name like concat('%', #{query.userName}, '%')
and u.user_phone like concat('%', #{query.userPhone}, '%')
