mapper.select:
select from hub_menu m where is_protected = 0 and m.menu_status = 1 and m.tenant_id in('#', #{tenantId}) and m.menu_type in('C', 'M') union select from hub_role r, hub_role_menu rm, hub_menu m where m.menu_status = 1 and m.tenant_id in('#', #{tenantId}) and m.menu_type in('C', 'M') and r.role_id = rm.role_id and rm.menu_id = m.menu_id and r.role_code in
#{item}
order by parentId, menuOrder

select s.scope_id scopeId, s.scope_name scopeName, rm.scope_id menu_scope, m.menu_id menuId, m.parent_id parentId, m.menu_name menuName, m.menu_order menuOrder, m.menu_type menuType, m.is_protected isProtected from hub_role r, hub_role_menu rm, hub_menu m left join hub_scope s on m.menu_module = s.scope_module and s.scope_status = 1 and s.tenant_id in ('#', #{tenantId}) where r.role_id = rm.role_id and rm.menu_id = m.menu_id and m.menu_status = 1 and m.is_protected = 1 and r.role_code in
#{item}
order by parentId, menuOrder
