mapper.select:
select
from hub_dept_post dp, hub_post p where p.tenant_id = #{tenantId} and dp.dept_id = #{query.deptId} and p.post_id = dp.post_id
from hub_post p where p.tenant_id = #{tenantId}
and p.post_type = #{query.postType}
and p.post_name like concat('%', #{query.postName}, '%')
order by p.post_type, p.post_id

select
from hub_dept_post dp, hub_post p where p.tenant_id = #{tenantId} and dp.dept_id = #{query.deptId} and p.post_id = dp.post_id
from hub_post p where p.tenant_id = #{tenantId}
and p.post_type = #{query.postType}
and p.post_name like concat('%', #{query.postName}, '%')
order by p.post_type, p.post_id

with recursive tree (post_id, parent_id) as(
    select t1.post_id, t1.parent_id from hub_post_diagram t1 where post_id = #{postId}
    union
    select t2.post_id, t2.parent_id from hub_post_diagram t2 join tree on t2.parent_id = tree.post_id
)
select distinct tree.post_id from tree JOIN hub_post u on tree.post_id = u.post_id

select d.dept_name || '/' || p.post_name from hub_dept d, hub_post p
where d.tenant_id = #{tenantId} and d.dept_id = #{deptId}
  and p.tenant_id = #{tenantId} and p.post_id = #{postId}
