mapper.select:
select DISTINCT on(u.user_id) d.dept_name deptName, p.post_name postName, u.user_id userId, u.user_name userName, u.user_phone userPhone, u.user_rank userRank from hub_user_role r join hub_user u on r.user_id = u.user_id left join hub_user_dept ud on u.user_id = ud.user_id and ud.is_default = 1 left join hub_dept d on ud.dept_id = d.dept_id left join hub_post p on ud.post_id = p.post_id where u.tenant_id = #{tenantId} and r.role_id = #{query.roleId}
and u.user_name like concat('%', #{query.userName}, '%')
and u.user_phone like concat('%', #{query.userPhone}, '%')

with uids as(select user_id from hub_user except select user_id from hub_user_role where role_id = #{query.roleId}) select DISTINCT on(u.user_id) d.dept_name deptName, p.post_name postName, u.user_id userId, u.user_name userName, u.user_phone userPhone, u.user_rank userRank from uids join hub_user u on uids.user_id = u.user_id left join hub_user_dept ud on u.user_id = ud.user_id and ud.is_default = 1 left join hub_dept d on ud.dept_id = d.dept_id left join hub_post p on ud.post_id = p.post_id where u.tenant_id = #{tenantId}
and u.user_name like concat('%', #{query.userName}, '%')
and u.user_phone like concat('%', #{query.userPhone}, '%')

mapper.insert:
with data(role_id, user_id) as ( values
(#{role.roleId}, #{userId})
) insert into hub_user_role(role_id, user_id) select data.role_id, data.user_id from data where exists (select 1 from hub_user where hub_user.user_id = data.user_id and hub_user.tenant_id = #{tenantId}) on conflict do nothing

mapper.select:
select r.role_code
from hub_role r, hub_user_role ur
where r.role_id = ur.role_id and ur.user_id = #{userId}
