mapper.select:
select u.*, d.dept_id, d.dept_name, p.post_id, p.post_name, ud.is_default, ud.is_leader from(select
from hub_user u where u.tenant_id = #{tenantId}
and u.user_name like concat('%', #{query.userName}, '%')
and u.user_phone like concat('%', #{query.userPhone}, '%')
and u.user_rank = #{query.userRank}
order by u.user_id limit #{query.pageSize} offset #{query.offset} ) u left join hub_user_dept ud on u.user_id = ud.user_id left join hub_dept d on ud.dept_id = d.dept_id left join hub_post p on ud.post_id = p.post_id

select count(1)
from hub_user u
where u.tenant_id = #{tenantId}

    and u.user_name like concat('%', #{query.userName}, '%')


    and u.user_phone like concat('%', #{query.userPhone}, '%')


    and u.user_rank = #{query.userRank}

select u.*,
d.dept_id, d.dept_name, p.post_id, p.post_name,
ud.is_default, ud.is_leader
from(with d as(
        with recursive tree (dept_id, parent_id) as(
            select t1.dept_id, t1.parent_id from hub_dept_diagram t1 where dept_id = #{query.deptId}
            union
            select t2.dept_id, t2.parent_id from hub_dept_diagram t2 join tree on t2.parent_id = tree.dept_id
        )
        select tree.dept_id, dept.dept_name, dept.tenant_id from tree JOIN hub_dept dept on tree.dept_id = dept.dept_id
    )
    select distinct on(u.user_id)

    from d
    join hub_user_dept ud on d.dept_id = ud.dept_id
    join hub_user u on ud.user_id = u.user_id
    where d.tenant_id = #{tenantId}

        and u.user_name like concat('%', #{query.userName}, '%')


        and u.user_phone like concat('%', #{query.userPhone}, '%')


        and u.user_rank = #{query.userRank}

    order by u.user_id
    limit #{query.pageSize} offset #{query.offset}
) u
left join hub_user_dept ud on u.user_id = ud.user_id
left join hub_dept d on ud.dept_id = d.dept_id
left join hub_post p on ud.post_id = p.post_id

with d as(
    with recursive tree (dept_id, parent_id) as(
        select t1.dept_id, t1.parent_id from hub_dept_diagram t1 where dept_id = #{query.deptId}
        union
        select t2.dept_id, t2.parent_id from hub_dept_diagram t2 join tree on t2.parent_id = tree.dept_id
    )
    select tree.dept_id, dept.dept_name, dept.tenant_id from tree JOIN hub_dept dept on tree.dept_id = dept.dept_id
)
select count(distinct u.user_id)
from d
join hub_user_dept ud on d.dept_id = ud.dept_id
join hub_user u on ud.user_id = u.user_id
where d.tenant_id = #{tenantId}

    and u.user_name like concat('%', #{query.userName}, '%')


    and u.user_phone like concat('%', #{query.userPhone}, '%')


    and u.user_rank = #{query.userRank}
