mapper.select:
select case d.parent_code when 'root' then '根字典' when 'group' then '根字典' else g.dict_name end groupName, case d.parent_code when 'root' then 'root' when 'group' then 'root' else g.dict_code end groupCode, case d.parent_code when 'root' then '根字典' else t.dict_name end typeName, case d.parent_code when 'root' then 'root' else t.dict_code end typeCode,
from hub_dict d left join hub_dict t on d.parent_code = t.dict_code left join hub_dict g on t.parent_code = g.dict_code where d.id in
#{item}

mapper.delete:
with ds as (select id from hub_dict where parent_code = #{groupCode}
            union all
            select d1.id from hub_dict d
            left join hub_dict d1 on d.dict_code = d1.parent_code
            where d.parent_code = #{groupCode}
)
delete from hub_dict d using ds where d.id = ds.id
