mapper.insert:
insert into hub_notice_user(notice_id, user_code)
select #{noticeId}, user_code from hub_user where user_status = 1 and tenant_id = #{tenantId}
on conflict(user_code, notice_id) do nothing

insert.if:
insert into hub_notice_user(notice_id, user_code) select #{noticeId}, u.user_code from hub_user_dept d join hub_user u on d.user_id = u.user_id where u.tenant_id = #{tenantId} and u.user_status = 1 and d.dept_id in
#{deptId}
on conflict(user_code, notice_id) do nothing

insert into hub_notice_user(notice_id, user_code) select #{noticeId}, u.user_code from hub_user_role r join hub_user u on r.user_id = u.user_id where u.tenant_id = #{tenantId} and u.user_status = 1 and r.role_id in
#{roleId}
on conflict(user_code, notice_id) do nothing

insert into hub_notice_user(notice_id, user_code) select #{noticeId}, u.user_code from hub_user u where u.tenant_id = #{tenantId} and u.user_id in
#{userId}
on conflict(user_code, notice_id) do nothing

mapper.update:
update hub_notice set stat_read = t.count
from (select count(1) from hub_notice_user where read_status >= 10 and notice_id = #{noticeId}) t
where notice_id = #{noticeId}
