mapper.update:
update db_info
set db_url      = #{dbUrl},
    db_user     = #{dbUser},
    db_passwd   = #{dbPasswd},
    update_time = #{accessTime}
where id = #{id}

mapper.select:
select sequence_name sequenceName, min_value minValue, max_value maxValue,
       increment_by incrementBy, last_number lastNumber
from info_schem.all_sequences where sequence_owner = #{owner}
